class Solution {
    func findSubstringInWraproundString(_ p: String) -> Int {
        
        if p.characters.count == 0 {
            return 0
        }
        
        if p.characters.count == 1 {
            return 1
        }
        
        var count = [Int](repeating:0, count:26)
        var maxCount = 0
        
        for (index, c) in p.characters.enumerated() {
            
            if index == 0 {
                maxCount = 1
                let countIndex = count.index(count.startIndex, offsetBy: c - Character("a"))
                count[countIndex] = max(count[countIndex], maxCount)
                continue
            }

            let beforeIndex = p.characters.index(before: p.index(p.startIndex, offsetBy:index))
            if c - p[beforeIndex] == 1 || p[beforeIndex] - c == 25 {
                maxCount += 1
            } else {
                maxCount = 1
            }
        
            let countIndex = count.index(count.startIndex, offsetBy: c - Character("a"))
            count[countIndex] = max(count[countIndex], maxCount)
        }
        
        return count.reduce(0, {
            return $0 + $1
        })
    }
}

extension Character {
     static func -(lhs: Character, rhs: Character) -> Int {
    
        let scalars = String(lhs).unicodeScalars
        let val = scalars[scalars.startIndex].value
        
        let scalars1 = String(rhs).unicodeScalars
        let val1 = scalars1[scalars1.startIndex].value
        
        return Int(Int(val) - Int(val1))
    }
}

